import React, { useState, useEffect } from 'react';
import './marqueue.css';

const Marqueue = () => {
  
  const holidayMessages = [
    { date: '12-25', message: 'Merry Christmas! Special discounts available for Christmas.' },
    { date: '01-01', message: 'Happy New Year! Start the year with fresh crackers!' },
    { date: '10-24', message: 'Happy Diwali! Crackers for a brighter celebration!' },
  ];

  
  const [message, setMessage] = useState('Crackers available in 365 days, we deliver to Tamilnadu, Kerala, Andhra, and other states. The Minimum order values are 3000 for Tamilnadu and above 5000 for all other states. Thank you!');

  
  useEffect(() => {
    
    const updateMessage = () => {
      const currentDate = new Date();
      const currentMonthDay = `${currentDate.getMonth() + 1}-${currentDate.getDate()}`;

     
      const holiday = holidayMessages.find(h => h.date === currentMonthDay);

      if (holiday) {
        setMessage(holiday.message);  
      } else {
        setMessage('Crackers available in 365 days, we deliver to Tamilnadu, Kerala, Andhra, and other states. The Minimum order values are 3000 for Tamilnadu and above 5000 for all other states. Thank you!');
      }
    };

    
    updateMessage();

    
    const interval = setInterval(updateMessage, 86400000);

    
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="marqueue-container">
      <div className="marqueue-content">
        <h2>{message}</h2>
      </div>
    </div>
  );
};

export default Marqueue;
