import React, { useState, useEffect } from 'react';
import './marqueue.css';

const Marqueue = () => {
  const [message, setMessage] = useState(
    'Crackers available in 365 days, we deliver to Tamilnadu, Kerala, Andhra, and other states. The Minimum order values are 3000 for Tamilnadu and above 5000 for all other states. Thank you!'
  );

  useEffect(() => {
    const updateMessage = () => {
      const currentDate = new Date();
      const currentMonth = currentDate.getMonth() + 1; 
      const currentDay = currentDate.getDate();

      // Check for Christmas
      if (currentMonth === 12) {
        setMessage('Merry Christmas! Special discounts available for Christmas.');
        return;
      }

      // Check for New Year
      if (currentMonth === 1 && currentDay === 1) {
        setMessage('Happy New Year! Start the year with fresh crackers!');
        return;
      }

      // Check for Diwali offer period (Jan 2 to Nov 30)
      if (
        (currentMonth > 1 && currentMonth < 12) || 
        (currentMonth === 1 && currentDay > 1)
      ) {
        setMessage('Happy Diwali! Crackers for a brighter celebration!');
        return;
      }

      // Default message
      setMessage(
        'Crackers available in 365 days, we deliver to Tamilnadu, Kerala, Andhra, and other states. The Minimum order values are 3000 for Tamilnadu and above 5000 for all other states. Thank you!'
      );
    };

    // Update message immediately
    updateMessage();

    // Set interval to update message daily
    const interval = setInterval(updateMessage, 86400000);

    // Cleanup interval on component unmount
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="marqueue-container">
      <div className="marqueue-content">
      <img src='assets/rocket.webp' alt="rocket" className='rocket'style={{height:"55px",width:"50px"}}/>
        <h2>{message}</h2>
      </div>
    </div>
  );
};

export default Marqueue;
